<?php

namespace Magazento\SettingsBundle\Repository;

use Magazento\SettingsBundle\Document\Apple;
use Doctrine\ODM\MongoDB\DocumentRepository;

/**
 * AppleRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AppleRepository extends DocumentRepository
{
    
    public function removeByUserId($userId) {
        return $this->createQueryBuilder()
                        ->remove()
                        ->field('user_id')->equals($userId)
                        ->getQuery()
                        ->execute();
    }
    
}